import { Node, SyntaxKind } from 'ts-morph';
import { CommentUtils, type CommentElement } from './comment-utils';

export interface MethodChainElement {
  kind: 'method';
  node: Node;
}

export interface CommentChainElement {
  kind: 'comment';
  node: CommentElement;
}

export type ChainElement = MethodChainElement | CommentChainElement;

/**
 * A class to collect method chains and their associated comments from TypeScript code
 */
export class MethodChainCollector {
  private debug: boolean;

  constructor(debug = false) {
    this.debug = debug;
  }

  /**
   * Add comment elements to the chain
   * @param node The node to extract comments from
   * @param indentLevel The indentation level
   * @returns Array of comment chain elements
   */
  private addCommentElements(node: Node, indentLevel: number): CommentChainElement[] {
    return CommentUtils.getAllComments(node, indentLevel).map(
      (c): CommentChainElement => ({ kind: 'comment', node: c })
    );
  }

  /**
   * Add a method element to the chain
   * @param node The method node
   * @returns A method chain element
   */
  private addMethodElement(node: Node): MethodChainElement {
    return { kind: 'method', node };
  }

  /**
   * Log debug information about chain elements
   * @param rootNode The root node of the chain
   * @param chain The collected chain elements
   */
  private logChainElementsDebug(rootNode: Node, chain: ChainElement[]): void {
    if (!this.debug) return;

    const sourceFile = rootNode.getSourceFile();
    console.log(`Method chain starting at position ${rootNode.getPos()} in ${sourceFile.getFilePath()}`);

    chain.forEach((element, index) => {
      if (element.kind === 'method') {
        const callExpr = element.node;
        const lineAndCol = sourceFile.getLineAndColumnAtPos(callExpr.getPos());
        console.log(`[${index}] Method: ${callExpr.getText().slice(0, 50)}... at line ${lineAndCol.line}`);
      } else {
        const comment = element.node;
        console.log(`[${index}] ${comment.type}: ${comment.text} at line ${comment.line}`);
      }
    });
  }

  /**
   * Collect method chain calls and their associated comments in the reverse order (upwards)
   * @param rootNode The root node of the method chain
   * @returns Ordered array of chain elements (methods and comments)
   */
  public collectChainMethodCallsUpwards(rootNode: Node): ChainElement[] {
    // First, let's get all the nodes in the method chain
    const methodNodes: Node[] = [];
    let current: Node | undefined = rootNode;

    while (current && Node.isCallExpression(current)) {
      methodNodes.push(current);

      // Navigate to the previous call expression in the chain
      const parent = current.getParent();
      if (parent && Node.isPropertyAccessExpression(parent)) {
        const grandParent = parent.getParent();
        if (grandParent && Node.isCallExpression(grandParent)) {
          current = grandParent;
        } else {
          break;
        }
      } else {
        break;
      }
    }

    // Now collect all comments in the entire chain's range
    // const sourceFile = rootNode.getSourceFile();
    // const chainStart = rootNode.getPos();
    // const chainEnd = methodNodes.length > 0 ? methodNodes[methodNodes.length - 1].getEnd() : rootNode.getEnd();

    // Get all comments within this range
    const allComments = CommentUtils.getMethodChainComments(rootNode, 0);

    // Create combined chain of methods and comments in order of appearance
    const combinedElements: { element: ChainElement; pos: number }[] = [
      // Add all method nodes with their positions
      ...methodNodes.map(node => ({
        element: this.addMethodElement(node),
        pos: node.getPos()
      })),

      // Add all comments with their positions
      ...allComments.map(comment => ({
        element: { kind: 'comment' as const, node: comment },
        pos: comment.pos
      }))
    ];

    // Sort by position to maintain the original order in the source code
    combinedElements.sort((a, b) => a.pos - b.pos);

    // Extract just the elements in the correct order
    const chain = combinedElements.map(item => item.element);

    this.logChainElementsDebug(rootNode, chain);
    return chain;
  }

  /**
   * Collect method chain calls and their associated comments in the correct order
   * @param rootNode The root node of the method chain
   * @returns Ordered array of chain elements (methods and comments)
   */
  public collectChainMethodCallsDownwards(rootNode: Node): ChainElement[] {
    // First, let's get all the nodes in the method chain
    const methodNodes: Node[] = [];
    let current: Node | undefined = rootNode;

    while (current && Node.isCallExpression(current)) {
      methodNodes.push(current);

      // Navigate to the next call expression in the chain
      const parent = current.getParent();
      if (
        parent &&
        Node.isPropertyAccessExpression(parent) &&
        Node.isCallExpression(parent.getParent())
      ) {
        current = parent.getParent();
      } else {
        break;
      }
    }

    // Now collect all comments in the entire chain's range
    // const sourceFile = rootNode.getSourceFile();
    // const chainStart = methodNodes.length > 0 ? methodNodes[methodNodes.length - 1].getPos() : rootNode.getPos();
    // const chainEnd = rootNode.getEnd();

    // Get all comments within this range
    const allComments = CommentUtils.getMethodChainComments(rootNode, 0);

    // Create combined chain of methods and comments in order of appearance
    const combinedElements: { element: ChainElement; pos: number }[] = [
      // Add all method nodes with their positions
      ...methodNodes.map(node => ({
        element: this.addMethodElement(node),
        pos: node.getPos()
      })),

      // Add all comments with their positions
      ...allComments.map(comment => ({
        element: { kind: 'comment' as const, node: comment },
        pos: comment.pos
      }))
    ];

    // Sort by position to maintain the original order in the source code
    combinedElements.sort((a, b) => a.pos - b.pos);

    // Extract just the elements in the correct order
    const chain = combinedElements.map(item => item.element);

    this.logChainElementsDebug(rootNode, chain);
    return chain;
  }

  /**
   * Get a flat array of all chain elements in a source file
   * @param sourceFile The source file to analyze
   * @returns Array of all chain elements in the file
   */
  public collectAllChains(sourceFile: Node): ChainElement[] {
    const allChains: ChainElement[] = [];

    // Find all call expressions that might be the start of a method chain
    const callExpressions = sourceFile.getDescendantsOfKind(SyntaxKind.CallExpression);

    for (const callExpr of callExpressions) {
      // Check if this is the start of a chain (not part of another chain)
      const parent = callExpr.getParent();
      if (
        !parent ||
        !Node.isPropertyAccessExpression(parent) ||
        !parent.getParent() ||
        !Node.isCallExpression(parent.getParent())
      ) {
        // This call expression might be the start of a chain
        const chain = this.collectChainMethodCallsDownwards(callExpr);
        if (chain.length > 1) {
          // Only add chains with more than one element
          allChains.push(...chain);
        }
      }
    }

    return allChains;
  }
}

