/**
 * Create indentation string based on level
 * @param level Indentation level
 * @param config Formatter config
 * @returns Indentation string
 */
export function createIndentation(level: number, config: BuilderFormatterConfig): string {
  if (isNaN(level) || !isFinite(level) || level < 0) {
    logger.warn(`Invalid indentation level: ${level}, using 0 instead`);
