import { Node, Type } from 'ts-morph';
import type { BuilderChainUtils } from './builder-chain-utils';

/**
 * Analyzes builder chains to determine indentation levels for each method call.
 */
export class IndentationAnalyzer {
  private readonly chainUtils: BuilderChainUtils;
  private readonly config: any;
  private methodIndentLevels: Map<Node, number> = new Map();
  private currentIndentLevel: number = 1;
  private builderStack: { type: Type; level: number }[] = [];

  /**
   * @param chainUtils Utility for builder chain type analysis
   * @param config Formatter configuration
   */
  constructor(chainUtils: BuilderChainUtils, config: any) {
    this.chainUtils = chainUtils;
    this.config = config;
  }

  /**
   * Determines indentation levels for method calls in a builder chain.
   * @param methodCalls Array of method call nodes in the chain
   * @returns Map of Node to indentation level
   */
  public analyze(methodCalls: Node[]): Map<Node, number> {
    this.resetState();
    try {
      for (const methodCall of methodCalls) {
        if (!Node.isCallExpression(methodCall)) continue;
        const expression = methodCall.getExpression();
        if (!Node.isPropertyAccessExpression(expression)) {
          this.handleDefaultMethod(methodCall);
          continue;
        }
        const methodName = expression.getName();
        const callerType = this.chainUtils.getCallerType(methodCall);
        const returnType = methodCall.getType();
        if (this.isDoneOrBuild(methodName)) {
          this.handleDoneOrBuild();
        } else if (this.isNestedBuilder(callerType, returnType)) {
          this.handleNestedBuilder(returnType);
        }
        this.methodIndentLevels.set(methodCall, this.currentIndentLevel);
      }
      return this.methodIndentLevels;
    } catch (error) {
      throw new Error('Failed to determine method indentation levels: ' + (error instanceof Error ? error.message : String(error)));
    }
  }

  /** Resets the analyzer state for a new analysis. */
  private resetState(): void {
    this.methodIndentLevels.clear();
    this.currentIndentLevel = 1;
    this.builderStack = [];
  }

  /** Handles 'done' or 'build' method calls. */
  private handleDoneOrBuild(): void {
    if (this.builderStack.length > 0) this.builderStack.pop();
    this.currentIndentLevel = Math.max(1, this.builderStack.length);
  }

  /** Handles nested builder method calls. */
  private handleNestedBuilder(returnType: Type): void {
    this.builderStack.push({ type: returnType, level: this.currentIndentLevel + 1 });
    this.currentIndentLevel++;
  }

  /** Handles default (non-builder) method calls. */
  private handleDefaultMethod(methodCall: Node): void {
    this.methodIndentLevels.set(methodCall, 0);
  }

  /** Checks if a method is 'done' or 'build'. */
  private isDoneOrBuild(methodName: string): boolean {
    return methodName === 'done' || methodName === 'build';
  }

  /** Checks if a method call returns a nested builder. */
  private isNestedBuilder(callerType: Type, returnType: Type): boolean {
    return (
      !this.chainUtils.areTypesEqual(callerType, returnType) &&
      this.chainUtils.hasBaseType(returnType, this.config.baseBuilderTypes.nestedBuilder)
    );
  }

  /** Gets the last analyzed indentation levels. */
  public getIndentLevels(): Map<Node, number> {
    return this.methodIndentLevels;
  }
}

