/**
 * Test utility to visualize how comments are associated with method chains
 */
export class CommentExtractionTester {
  private project: Project;

  constructor() {
    this.project = new Project();
  }

  /**
   * Creates a source file with the given content for testing
   * @param content The TypeScript code to test
   * @returns The created source file
   */
  public createTestFile(content: string): SourceFile {
    return this.project.createSourceFile('test.ts', content);
  }

  /**
   * Visualizes the source code with annotated comment types and positions
   * @param sourceFile The source file to visualize
   * @returns A string representation with comment annotations
   */
  public visualizeComments(sourceFile: SourceFile): string {
    const fullText = sourceFile.getFullText();
    const lines = fullText.split('\n');
    const result: string[] = [];

    // Use public methods instead of private ones
    // Extract comments using getAllComments which is public
    const allComments: CommentElement[] = [];

    // Process the entire file as a node to get all comments
    const allNodes = sourceFile.getDescendants();
    for (const node of allNodes) {
      // Get comments for each node and filter duplicates
      const nodeComments = CommentUtils.getAllComments(node, 0);
      for (const comment of nodeComments) {
        // Check if comment already exists in our collection
        if (!allComments.some(c => c.pos === comment.pos && c.end === comment.end)) {
          allComments.push(comment);
        }
      }
    }

    // Create the visualization
    for (let i = 0; i < lines.length; i++) {
      const lineNumber = i + 1;
      const line = lines[i];

      // Find comments on this line
      const commentsOnLine = allComments.filter(c => c.line === lineNumber);

      if (commentsOnLine.length > 0) {
        // Add an annotation for each comment
        const annotations = commentsOnLine.map(
          c => `[${c.type}:${c.pos}-${c.end}]`
        ).join(' ');

        result.push(`${lineNumber}: ${line} ${annotations}`);
      } else {
        result.push(`${lineNumber}: ${line}`);
      }
    }

    return result.join('\n');
