import {
  CallExpression,
  CommentRange,
  Node,
  SourceFile,
  SyntaxKind,
  ts
} from 'ts-morph';

/**
 * Extended CommentRange interface with properties needed for our implementation
 */
export interface CommentRangeWithPos extends CommentRange {
  pos: number;
  end: number;
  kind: ts.SyntaxKind.SingleLineCommentTrivia | ts.SyntaxKind.MultiLineCommentTrivia;
  hasTrailingNewLine?: boolean;
  getKind: () => ts.CommentKind;
}

/**
 * Represents a single-line comment (// comment)
 */
export interface SingleLineComment {
  type: 'SingleLineComment';
  text: string;
  pos: number;
  end: number;
  line: number;
  indentLevel: number;
}

/**
 * Represents a multi-line comment (/* comment *\/)
 */
export interface MultiLineComment {
  type: 'MultiLineComment';
  text: string;
  pos: number;
  end: number;
  line: number;
  indentLevel: number;
}

/**
 * Represents a JSDoc comment (/** doc comment *\/)
 */
export interface DocComment {
  type: 'DocComment';
  text: string;
  pos: number;
  end: number;
  line: number;
  indentLevel: number;
}

/**
 * Union type for all comment types
 */
export type CommentElement = SingleLineComment | MultiLineComment | DocComment;

/**
 * Utility class for extracting and handling comments in TypeScript code
 */
export class CommentUtils {
  /**
   * Extracts all comments associated with a node, including leading, trailing, and range comments
   * @param node The Node to extract comments from
   * @param indentLevel The indentation level for formatting
   * @returns Array of comment elements ordered by their position in the source code
   */
  public static getAllComments(node: Node, indentLevel: number): CommentElement[] {
    const sourceFile = node.getSourceFile();
    const fullText = sourceFile.getFullText();
    const nodePos = node.getPos();
    const nodeEnd = node.getEnd();

    // Get a range around the node to capture comments that might be related
    // We'll look at a reasonable range before and after the node
    const rangeStart = Math.max(0, nodePos - 500); // Look up to 500 chars before
    const rangeEnd = Math.min(fullText.length, nodeEnd + 500); // And 500 chars after

    // Get all comments in this range
    const commentRanges = this.getCommentRangesInRange(sourceFile, rangeStart, rangeEnd);

    // Associate comments with the node based on their position and context
    const relevantComments: CommentElement[] = [];

    // Process each comment range into a structured comment object
    for (const commentRange of commentRanges) {
      const commentText = fullText.substring(commentRange.pos, commentRange.end);
      const lineNumber = sourceFile.getLineAndColumnAtPos(commentRange.pos).line;

      // Skip comments that are clearly not related to this node
      // This heuristic can be adjusted based on your specific needs
      if (node.getKind() === SyntaxKind.CallExpression) {
        const callExpr = node as CallExpression;
        const callExprLine = sourceFile.getLineAndColumnAtPos(callExpr.getPos()).line;

        // For method chains, we care about comments that are:
        // 1. On the same line as the call expression
        // 2. On the line directly above the call expression
        // 3. Trailing after the call expression
        const commentLine = lineNumber;

        if (Math.abs(commentLine - callExprLine) > 3 && commentRange.pos < nodePos) {
          // Skip comments that are too far away from the call expression
          continue;
        }
      }

      const comment = this.createCommentElement(commentText, commentRange, lineNumber, indentLevel);
      if (comment) {
        relevantComments.push(comment);
      }
    }

    // Sort comments by their position in the source file to maintain the original order
    return relevantComments.sort((a, b) => a.pos - b.pos);
  }

  /**
   * Get all comment ranges within a specified range in the source file
   */
  private static getCommentRangesInRange(
    sourceFile: SourceFile,
    rangeStart: number,
    rangeEnd: number
  ): CommentRangeWithPos[] {
    // Get full text to use for scanning
    const fullText = sourceFile.getFullText();
    const allCommentRanges: CommentRangeWithPos[] = [];

    // Get the TypeScript scanner to identify comments
    const scanner = ts.createScanner(
      ts.ScriptTarget.Latest,
      /*skipTrivia*/ false,
      ts.LanguageVariant.Standard,
      fullText
    );

    // Set the scanner to the start of our range
    scanner.setTextPos(rangeStart);

    // Collect all comments within the range
    let token = scanner.scan();
    while (token !== ts.SyntaxKind.EndOfFileToken && scanner.getTextPos() <= rangeEnd) {
      const tokenPos = scanner.getTokenPos();

      // Check if we found a comment
      if (token === ts.SyntaxKind.SingleLineCommentTrivia ||
        token === ts.SyntaxKind.MultiLineCommentTrivia) {
        const commentEnd = scanner.getTextPos();

        allCommentRanges.push({
          kind: token,
          pos: tokenPos,
          end: commentEnd,
          hasTrailingNewLine: fullText.substr(commentEnd, 1) === '\n',
          getKind: () => token as ts.CommentKind
        } as CommentRangeWithPos);
      }

      token = scanner.scan();
    }

    return allCommentRanges;
  }

  /**
   * Creates a typed comment element based on the comment text and range
   */
  private static createCommentElement(
    commentText: string,
    commentRange: CommentRangeWithPos,
    lineNumber: number,
    indentLevel: number
  ): CommentElement | null {
    // Determine the type of comment
    if (commentRange.kind === ts.SyntaxKind.SingleLineCommentTrivia) {
      // Single line comment
      return {
        type: 'SingleLineComment',
        text: commentText.replace(/^\/\/\s?/, '').trim(), // Remove the // prefix
        pos: commentRange.pos,
        end: commentRange.end,
        line: lineNumber,
        indentLevel
      };
    } else if (commentRange.kind === ts.SyntaxKind.MultiLineCommentTrivia) {
      // Check if it's a JSDoc comment or a regular multi-line comment
      if (commentText.startsWith('/**')) {
        // JSDoc comment
        return {
          type: 'DocComment',
          text: commentText
            .replace(/^\/\*\*/, '')
            .replace(/\*\/$/, '')
            .split('\n')
            .map(line => line.replace(/^\s*\*\s?/, ''))
            .join('\n')
            .trim(),
          pos: commentRange.pos,
          end: commentRange.end,
          line: lineNumber,
          indentLevel
        };
      } else {
        // Regular multi-line comment
        return {
          type: 'MultiLineComment',
          text: commentText
            .replace(/^\/\*/, '')
            .replace(/\*\/$/, '')
            .trim(),
          pos: commentRange.pos,
          end: commentRange.end,
          line: lineNumber,
          indentLevel
        };
      }
    }

    return null;
  }

  /**
   * Gets leading comments for a node (comments that appear before the node)
   */
  public static getLeadingComments(node: Node, indentLevel: number): CommentElement[] {
    const sourceFile = node.getSourceFile();
    const fullText = sourceFile.getFullText();
    const nodePos = node.getPos();

    // Look at a reasonable range before the node
    const rangeStart = Math.max(0, nodePos - 200);

    // Get all comments in this range
    const commentRanges = this.getCommentRangesInRange(sourceFile, rangeStart, nodePos);

    // Process each comment range
    const leadingComments: CommentElement[] = [];
    for (const commentRange of commentRanges) {
      const commentText = fullText.substring(commentRange.pos, commentRange.end);
      const lineNumber = sourceFile.getLineAndColumnAtPos(commentRange.pos).line;

      const comment = this.createCommentElement(commentText, commentRange, lineNumber, indentLevel);
      if (comment) {
        leadingComments.push(comment);
      }
    }

    return leadingComments.sort((a, b) => a.pos - b.pos);
  }

  /**
   * Gets trailing comments for a node (comments that appear after the node on the same line)
   */
  public static getTrailingComments(node: Node, indentLevel: number): CommentElement[] {
    const sourceFile = node.getSourceFile();
    const fullText = sourceFile.getFullText();
    const nodeEnd = node.getEnd();

    // Look at a reasonable range after the node (to the end of the line)
    const lineAndColumn = sourceFile.getLineAndColumnAtPos(nodeEnd);
    // Use alternative approach to get line end since getLineEndOfPosition isn't available
    const lineEndPos = this.getLineEndPosition(fullText, nodeEnd);

    // Get all comments in this range
    const commentRanges = this.getCommentRangesInRange(sourceFile, nodeEnd, lineEndPos);

    // Process each comment range
    const trailingComments: CommentElement[] = [];
    for (const commentRange of commentRanges) {
      const commentText = fullText.substring(commentRange.pos, commentRange.end);
      const lineNumber = sourceFile.getLineAndColumnAtPos(commentRange.pos).line;

      const comment = this.createCommentElement(commentText, commentRange, lineNumber, indentLevel);
      if (comment) {
        trailingComments.push(comment);
      }
    }

    return trailingComments;
  }

  /**
   * Helper method to find the position of the end of the line
   */
  private static getLineEndPosition(text: string, position: number): number {
    let pos = position;
    while (pos < text.length && text[pos] !== '\n' && text[pos] !== '\r') {
      pos++;
    }
    return pos;
  }

  /**
   * Determines if a comment is positioned before a specific method in a chain
   * This can be used to associate comments with the right method in a chain
   */
  public static isCommentBeforeMethod(comment: CommentElement, method: Node): boolean {
    return comment.pos < method.getPos();
  }

  /**
   * Gets all comments associated with a method chain, in their correct order
   * @param chainRoot The root node of the method chain
   * @param indentLevel The indentation level
   */
  public static getMethodChainComments(chainRoot: Node, indentLevel: number): CommentElement[] {
    // Get the source file and all method call expressions in the chain
    const sourceFile = chainRoot.getSourceFile();
    const allMethodCalls: Node[] = [];

    // Collect all method calls in the chain
    let current: Node | undefined = chainRoot;
    while (current && Node.isCallExpression(current)) {
      allMethodCalls.push(current);
      const parent = current.getParent();

      // Safely navigate through the parent chain with proper null checks
      if (parent && Node.isPropertyAccessExpression(parent)) {
        const parentParent = parent.getParent();

        if (parentParent && Node.isPropertyAccessExpression(parentParent)) {
          const grandParent = parentParent.getParent();

          if (grandParent && Node.isCallExpression(grandParent)) {
            current = grandParent;
          } else {
            break;
          }
        } else {
          break;
        }
      } else {
        break;
      }
    }

    // Define the range that encompasses the entire method chain
    // Only proceed if we have method calls to work with
    if (allMethodCalls.length === 0) {
      return [];
    }

    const chainStart = allMethodCalls[allMethodCalls.length - 1].getPos();
    const chainEnd = allMethodCalls[0].getEnd();

    // Get all comments within this range
    const commentRanges = this.getCommentRangesInRange(sourceFile, chainStart, chainEnd);
    const fullText = sourceFile.getFullText();

    const allComments = commentRanges
      .map(commentRange => {
        const commentText = fullText.substring(commentRange.pos, commentRange.end);
        const lineNumber = sourceFile.getLineAndColumnAtPos(commentRange.pos).line;
        return this.createCommentElement(commentText, commentRange, lineNumber, indentLevel);
      })
      .filter((comment): comment is CommentElement => comment !== null)
      .sort((a, b) => a.pos - b.pos);

    return allComments;
  }
}

import { Node, SourceFile, SyntaxKind, Type } from "ts-morph";
import { Logger } from "../../../utils";
import type { BuilderFormatterConfig } from "../builder-formatter-config";
import { MethodChainCollector, type ChainElement } from "./method-chain-collector";

const logger = Logger.getInstance().forModule('builder-chain-utils');

export class BuilderChainUtils {
  private walkChainDownwards: boolean;

  constructor(private config: BuilderFormatterConfig, walkChainDownwards = true) {
    this.walkChainDownwards = walkChainDownwards;
  }

  public isBuilderChain(node: Node): boolean {
    try {
      if (!Node.isCallExpression(node)) {
        return false;
      }

      const expression = node.getExpression();

      if (Node.isPropertyAccessExpression(expression)) {
        const methodName = expression.getName();

        if (methodName === 'build') {
          const parent = node.getParent();
          if (parent && (Node.isVariableDeclaration(parent) || Node.isReturnStatement(parent))) {
            return false;
          }
        }

        if (this.config.methodNames.returnToParent.includes(methodName)) {
          return true;
        }
      }

      try {
        const type = node.getType();

        if (this.hasBaseType(type, this.config.baseBuilderTypes.rootBuilder)) {
          return true;
        }

        if (this.hasBaseType(type, this.config.baseBuilderTypes.nestedBuilder)) {
          return true;
        }

        const parent = node.getParent();
        if (parent) {
          if (Node.isVariableDeclaration(parent)) {
            const initializer = parent.getInitializer();
            if (initializer === node) {
              const varType = parent.getType();
              if (
                this.hasBaseType(varType, this.config.baseBuilderTypes.rootBuilder)
                || this.hasBaseType(varType, this.config.baseBuilderTypes.nestedBuilder)
              ) {
                return true;
              }
            }
          }
        }
      } catch (error) {
        logger.debug(`Error getting type: ${error instanceof Error ? error.message : String(error)}`);
      }

      return false;
    } catch (error) {
      logger.warn(`Error in isBuilderChain: ${error instanceof Error ? error.message : String(error)}`);
      return false;
    }
  }

  public findBuilderChains(sourceFile: SourceFile): Node[] {
    logger.debug(`Finding builder chains in ${sourceFile.getFilePath()}`);
    const builderChains: Node[] = [];
    const callExpressions = sourceFile.getDescendantsOfKind(SyntaxKind.CallExpression);
    for (const callExpr of callExpressions) {
      const parent = callExpr.getParent();
      if (parent && (Node.isCallExpression(parent) || Node.isPropertyAccessExpression(parent))) continue;
      if (this.isBuilderChain(callExpr)) builderChains.push(callExpr);
    }
    return builderChains;
  }

  public hasBaseType(type: Type, baseTypeName: string): boolean {
    const typeText = type.getText();
    const isExactMatch = new RegExp(`\\b${baseTypeName}\\b`).test(typeText);
    const isGenericMatch = new RegExp(`\\b${baseTypeName}<`).test(typeText);
    if (isExactMatch || isGenericMatch) return true;
    const baseTypes = type.getBaseTypes();
    if (baseTypes) {
      for (const baseType of baseTypes) {
        const baseTypeText = baseType.getText();
        const isBaseExactMatch = new RegExp(`\\b${baseTypeName}\\b`).test(baseTypeText);
        const isBaseGenericMatch = new RegExp(`\\b${baseTypeName}<`).test(baseTypeText);
        if (isBaseExactMatch || isBaseGenericMatch) return true;
        if (this.hasBaseType(baseType, baseTypeName)) return true;
      }
    }
    return false;
  }

  public areTypesEqual(type1: Type, type2: Type): boolean {
    return type1.getText() === type2.getText();
  }

  public getCallerType(methodCall: Node): Type {
    if (Node.isCallExpression(methodCall)) {
      const expression = methodCall.getExpression();
      if (Node.isPropertyAccessExpression(expression)) {
        return expression.getExpression().getType();
      }
    }
    return methodCall.getType();
  }

  public findRootOfChain(node: Node): Node {
    const expression = Node.isCallExpression(node) ? node.getExpression() : null;
    if (expression && Node.isIdentifier(expression)) return node;
    let currentNode = node;
    while (Node.isCallExpression(currentNode)) {
      const expr = currentNode.getExpression();
      if (Node.isPropertyAccessExpression(expr)) {
        const objectExpr = expr.getExpression();
        if (Node.isIdentifier(objectExpr)) return currentNode;
        if (Node.isCallExpression(objectExpr)) currentNode = objectExpr;
        else break;
      } else break;
    }
    return currentNode;
  }

  public getMethodCallsInChain(rootNode: Node): ChainElement[] {
    try {
      // Create the collector with debug enabled
      const methodChainCollector = new MethodChainCollector(true);

      // Get the source file containing this node
      const sourceFile = rootNode.getSourceFile();
      logger.debug(`Looking for method calls in file: ${sourceFile.getFilePath()}`);

      // Get all call expressions in the file that might be related to our chain
      const callExpressions = sourceFile.getDescendantsOfKind(SyntaxKind.CallExpression);

      // Find the call expression that matches our root node to determine the chain
      for (const callExpr of callExpressions) {
        if (callExpr.getPos() === rootNode.getPos()) {
          // Found the matching call expression, collect the chain
          logger.debug(`Found matching call expression at position ${callExpr.getPos()}`);

          // Select the appropriate collection method based on direction
          let chain: ChainElement[] = [];
          if (this.walkChainDownwards) {
            chain = methodChainCollector.collectChainMethodCallsDownwards(callExpr);
          } else {
            chain = methodChainCollector.collectChainMethodCallsUpwards(callExpr);
          }

          // Log results for debugging
          this.logChainElementsDebug(rootNode, chain);

          // return chain;
        }
      }

      // If we reach here, we couldn't find the matching call expression
      // Fall back to the original approach
      logger.debug(`No matching call expression found, using direct approach`);
      let chain: ChainElement[] = [];
      if (this.walkChainDownwards) {
        chain = methodChainCollector.collectChainMethodCallsDownwards(rootNode);
      } else {
        chain = methodChainCollector.collectChainMethodCallsUpwards(rootNode);
      }

      this.logChainElementsDebug(rootNode, chain);
      return chain;
    } catch (error) {
      logger.warn(`Error collecting method chain: ${error instanceof Error ? error.message : String(error)}`);
      return [];
    }
  }

  /**
   * Log debug information for chain elements (method calls and comments).
   */
  private logChainElementsDebug(rootNode: Node, chainElements: ChainElement[]): void {
    const textLimit = 20;
    const commentLimit = 50;
    const showText = false;
    logger.debug(`getMethodCallsInChain: rootNode kind=${rootNode.getKindName()}, ${showText ? `text='${rootNode.getText().substring(0, textLimit)}...'` : ''}`);
    logger.debug(`getMethodCallsInChain: Found ${chainElements.length} elements in chain:`);
    chainElements.forEach((el, idx) => {
      if (el.kind === 'comment') {
        logger.debug(`  [${idx}] COMMENT: ${el.node.text?.substring(0, commentLimit)}...`);
      } else {
        let methodName = 'root';
        if (Node.isCallExpression(el.node)) {
          const expr = el.node.getExpression();
          if (Node.isPropertyAccessExpression(expr)) {
            methodName = expr.getName();
          }
        }
        logger.debug(`  [${idx}] kind=${el.node.getKindName()}, method=${methodName}, ${showText ? `text='${el.node.getText().substring(0, textLimit)}...'` : ''}`);
      }
    });
  }
}

import path from 'node:path';
import { Node, Project } from 'ts-morph';
import { Logger } from '../../../utils';
import type { Configuration } from '../../config';
import type { BeforeAfterText, FormattingResult } from '../../types';
import { BuilderChainUtils } from './builder-chain-utils';

/**
 * Handles AST-based file formatting for builder chains.
 */
export class AstFileFormatter {
  private readonly chainUtils: BuilderChainUtils;
  private readonly config: Configuration;
  private readonly logger = Logger.getInstance().forModule('ast-file-formatter');

  constructor(chainUtils: BuilderChainUtils, config: Configuration) {
    this.chainUtils = chainUtils;
    this.config = config;
  }

  /**
   * Formats a file using AST-based builder chain formatting, with detailed debug output and before/after reporting.
   */
  public async formatFile(filePath: string, formatBuilderChain: (node: Node) => string): Promise<FormattingResult> {
    this.logger.debug(`AST-based formatter processing file: ${filePath}`);
    const { dryRun, detailLevel } = this.config;
    this.logger.debug(`Dry run mode: ${dryRun}`);
    if (dryRun === undefined) {
      process.exit(1);
    }
    try {
      this.logger.debug(`Creating TypeScript project with tsconfig: ${this.config.tsConfigPath || 'default'}`);
      const project = new Project({
        tsConfigFilePath: this.config.tsConfigPath || path.join(process.cwd(), 'tsconfig.json'),
      });
      const sourceFile = project.addSourceFileAtPath(filePath);
      this.logger.debug(`Added source file to project: ${filePath}`);
      const builderChains = this.chainUtils.findBuilderChains(sourceFile);
      this.logger.debug(`Found ${builderChains.length} builder chains in file`);
      let changesMade = false;
      let changeCount = 0;
      const beforeAfterTexts: BeforeAfterText[] = [];
      for (const chain of builderChains) {
        const originalText = chain.getText();
        this.logger.debug(`Formatting builder chain: ${originalText.substring(0, 50)}${originalText.length > 50 ? '...' : ''}`);
        let formattedText = formatBuilderChain(chain);
        // Remove comment lines and blank lines for AST replacement
        formattedText = formattedText
          .split('\n')
          .filter(line => !line.trim().startsWith('//') && line.trim() !== '')
          .join('\n');
        if (formattedText !== originalText) {
          changesMade = true;
          changeCount++;
          this.logger.debug(`Changes made to builder chain (${changeCount})`);
          if (detailLevel > 0) {
            beforeAfterTexts.push({ before: originalText, after: formattedText });
          }
          if (!dryRun) {
            if (Node.isCallExpression(chain)) {
              try {
                chain.replaceWithText(formattedText);
                this.logger.debug(`Applied changes to source file (not in dry run mode)`);
              } catch (err) {
                this.logger.error(`Error replacing builder chain text: ${err instanceof Error ? err.message : String(err)}`);
              }
            } else {
              this.logger.error(`Attempted to replace text on non-CallExpression node: ${chain.getKindName()}`);
            }
          } else {
            this.logger.debug(`Skipped applying changes (dry run mode)`);
          }
        } else {
          this.logger.debug(`No changes needed for this builder chain`);
        }
      }
      if (changesMade && !dryRun) {
        this.logger.debug(`Saving changes to file: ${filePath}`);
        await sourceFile.save();
      } else if (changesMade && dryRun) {
        this.logger.debug(`Changes would be saved, but skipping due to dry run mode`);
      } else {
        this.logger.debug(`No changes to save for file: ${filePath}`);
      }
      return {
        filePath,
        changesMade,
        changeCount,
        beforeAfter: beforeAfterTexts.length > 0 ? beforeAfterTexts : undefined,
      };
    } catch (error: unknown) {
      this.logger.error(`Error in AST-based formatting: ${error instanceof Error ? error.message : String(error)}`);
      return {
        filePath,
        changesMade: false,
        error: error instanceof Error ? error : new Error(String(error)),
      };
    }
  }
}

import { Node, SourceFile } from 'ts-morph';
import { Logger } from '../../../utils';
import { Configuration, getConfig } from '../../config';
import type { FormattingResult } from '../../types';
import { BaseFormatter } from '../base-formatter';
import type { BuilderFormatterConfig } from '../builder-formatter-config';
import { DEFAULT_BTT_PRESET_CONFIG } from '../default-configurations';
import { AstFileFormatter } from './ast-file-formatter';
import { BuilderChainUtils } from './builder-chain-utils';
import type { FormattedNode } from './formatted-node';
import { IndentationAnalyzer } from './indentation-analyzer';
import { createIndentation } from './indentation-utils';

const logger = Logger.getInstance();
const moduleLogger = logger.forModule('ast-based-formatter');

/**
 * AST-based formatter for builder patterns.
 * Delegates AST and formatting logic to utility classes.
 */
export class AstBasedFormatter extends BaseFormatter {
  private builderConfig: BuilderFormatterConfig;
  private chainUtils: BuilderChainUtils;
  private globalConfig: Configuration;
  private indentationAnalyzer: IndentationAnalyzer;

  constructor(builderConfig?: BuilderFormatterConfig) {
    super();
    this.builderConfig = builderConfig || { ...DEFAULT_BTT_PRESET_CONFIG };
    this.globalConfig = getConfig();
    if (this.globalConfig.indentation) {
    this.builderConfig.formatting.spacesPerLevel = this.globalConfig.indentation;
  }
    this.chainUtils = new BuilderChainUtils(this.builderConfig);
this.indentationAnalyzer = new IndentationAnalyzer(this.chainUtils, this.builderConfig);
  }

  /**
   * Checks if a node is a builder chain.
   */
  public isBuilderChain(node: Node): boolean {
  return this.chainUtils.isBuilderChain(node);
}

  /**
   * Finds all builder chains in a source file.
   */
  public findBuilderChains(sourceFile: SourceFile): Node[] {
  return this.chainUtils.findBuilderChains(sourceFile);
}

  /**
   * Formats a builder chain node.
   * Throws if node is not a builder chain.
   */
  public formatBuilderChain(node: Node): string {
  if (!this.isBuilderChain(node)) throw new Error('Node is not a recognized builder chain');
  try {
    const rootNode = this.chainUtils.findRootOfChain(node);
    // Get chain elements (method calls and comments)
    const chainElements = this.chainUtils.getMethodCallsInChain(rootNode);
    // Extract only method call nodes for indentation
    const methodCalls: Node[] = chainElements.filter(el => el.kind === 'method').map(el => el.node);
    const methodIndentLevels = this.indentationAnalyzer.analyze(methodCalls);
    // Integrate comments and method calls in formatted output
    const formattedLines: string[] = [];
    for (const el of chainElements) {
      if (el.kind === 'comment') {
        const indentation = createIndentation(el.node.indentLevel, this.builderConfig);
        formattedLines.push(`${indentation}${el.node.text}`);
      } else {
        const indentLevel = methodIndentLevels.get(el.node) || 1;
        const methodText = this.getMethodCallText(el.node);
        const indentation = createIndentation(indentLevel, this.builderConfig);
        if (el.node === rootNode) {
          formattedLines.push(`${this.getNodeText(el.node)}`);
        } else {
          formattedLines.push(`${indentation}.${methodText}`);
        }
      }
    }
    return formattedLines.join('\n');
  } catch (error) {
    moduleLogger.error(`Error formatting builder chain: ${error instanceof Error ? error.message : String(error)}`);
    throw error;
  }
}

  /**
   * Get the text representation of a node.
   * Returns a formatted call expression or falls back to node.getText().
   */
  private getNodeText(node: Node): string {
  if (Node.isCallExpression(node)) {
    const expression = node.getExpression();
    if (!Node.isPropertyAccessExpression(expression)) {
      const args = node.getArguments();
      const argsText = args.map(arg => arg.getText()).join(', ');
      if (Node.isIdentifier(expression)) {
        return `${expression.getText()}(${argsText})`;
      }
    }
  }
  // Fallback: return the node's text
  return node.getText();
}

  private getMethodName(callExpr: Node): string | undefined {
  if (!Node.isCallExpression(callExpr)) return undefined;
  const expression = callExpr.getExpression();
  if (Node.isPropertyAccessExpression(expression)) {
    return expression.getName();
  }
  return undefined;
}

  private getCallArguments(callExpr: Node): Node[] {
  if (!Node.isCallExpression(callExpr)) return [];
  return callExpr.getArguments();
}

  private getMethodCallText(methodCall: Node): string {
  if (!Node.isCallExpression(methodCall)) return methodCall.getText();
  const methodName = this.getMethodName(methodCall);
  if (!methodName) return methodCall.getText();
  const args = this.getCallArguments(methodCall);
  const argsText = args.map(arg => arg.getText()).join(', ');
  return methodName + `(${argsText})`;
}

  private generateFormattedOutput(formattedNodes: FormattedNode[]): string {
  const formattedLines: string[] = [];
  const processedMethodCalls = new Set<Node>();
  for (const formattedNode of formattedNodes) {
    if (!formattedNode.isComment && formattedNode.node !== formattedNodes[0]?.node) {
      if (processedMethodCalls.has(formattedNode.node)) continue;
      processedMethodCalls.add(formattedNode.node);
    }
    const indentation = createIndentation(formattedNode.indentLevel, this.builderConfig);
    formattedLines.push(`${indentation}${formattedNode.text}`);
  }
  return formattedLines.join('\n');
}

  /**
   * Formats a file using AST-based builder chain formatting.
   */
  public async formatFile(filePath: string): Promise < FormattingResult > {
  const fileFormatter = new AstFileFormatter(this.chainUtils, this.globalConfig);
  return fileFormatter.formatFile(filePath, this.formatBuilderChain.bind(this));
}
}

