import { Node, SourceFile, SyntaxKind, Type } from "ts-morph";
import { Logger } from "../../../utils";
import type { BuilderFormatterConfig } from "../builder-formatter-config";
import { MethodChainCollector, type ChainElement } from "./method-chain-collector";

const logger = Logger.getInstance().forModule('builder-chain-utils');

export class BuilderChainUtils {
  private walkChainDownwards: boolean;

  constructor(private config: BuilderFormatterConfig, walkChainDownwards = true) {
    this.walkChainDownwards = walkChainDownwards;
  }

  public isBuilderChain(node: Node): boolean {
    try {
      if (!Node.isCallExpression(node)) {
        return false;
      }

      const expression = node.getExpression();

      if (Node.isPropertyAccessExpression(expression)) {
        const methodName = expression.getName();

        if (methodName === 'build') {
          const parent = node.getParent();
          if (parent && (Node.isVariableDeclaration(parent) || Node.isReturnStatement(parent))) {
            return false;
          }
        }

        if (this.config.methodNames.returnToParent.includes(methodName)) {
          return true;
        }
      }

      try {
        const type = node.getType();

        if (this.hasBaseType(type, this.config.baseBuilderTypes.rootBuilder)) {
          return true;
        }

        if (this.hasBaseType(type, this.config.baseBuilderTypes.nestedBuilder)) {
          return true;
        }

        const parent = node.getParent();
        if (parent) {
          if (Node.isVariableDeclaration(parent)) {
            const initializer = parent.getInitializer();
            if (initializer === node) {
              const varType = parent.getType();
              if (
                this.hasBaseType(varType, this.config.baseBuilderTypes.rootBuilder)
                || this.hasBaseType(varType, this.config.baseBuilderTypes.nestedBuilder)
              ) {
                return true;
              }
            }
          }
        }
      } catch (error) {
        logger.debug(`Error getting type: ${error instanceof Error ? error.message : String(error)}`);
      }

      return false;
    } catch (error) {
      logger.warn(`Error in isBuilderChain: ${error instanceof Error ? error.message : String(error)}`);
      return false;
    }
  }

  public findBuilderChains(sourceFile: SourceFile): Node[] {
    logger.debug(`Finding builder chains in ${sourceFile.getFilePath()}`);
    const builderChains: Node[] = [];
    const callExpressions = sourceFile.getDescendantsOfKind(SyntaxKind.CallExpression);
    for (const callExpr of callExpressions) {
      const parent = callExpr.getParent();
      if (parent && (Node.isCallExpression(parent) || Node.isPropertyAccessExpression(parent))) continue;
      if (this.isBuilderChain(callExpr)) builderChains.push(callExpr);
    }
    return builderChains;
  }

  public hasBaseType(type: Type, baseTypeName: string): boolean {
    const typeText = type.getText();
    const isExactMatch = new RegExp(`\\b${baseTypeName}\\b`).test(typeText);
    const isGenericMatch = new RegExp(`\\b${baseTypeName}<`).test(typeText);
    if (isExactMatch || isGenericMatch) return true;
    const baseTypes = type.getBaseTypes();
    if (baseTypes) {
      for (const baseType of baseTypes) {
        const baseTypeText = baseType.getText();
        const isBaseExactMatch = new RegExp(`\\b${baseTypeName}\\b`).test(baseTypeText);
        const isBaseGenericMatch = new RegExp(`\\b${baseTypeName}<`).test(baseTypeText);
        if (isBaseExactMatch || isBaseGenericMatch) return true;
        if (this.hasBaseType(baseType, baseTypeName)) return true;
      }
    }
    return false;
  }

  public areTypesEqual(type1: Type, type2: Type): boolean {
    return type1.getText() === type2.getText();
  }

  public getCallerType(methodCall: Node): Type {
    if (Node.isCallExpression(methodCall)) {
      const expression = methodCall.getExpression();
      if (Node.isPropertyAccessExpression(expression)) {
        return expression.getExpression().getType();
      }
    }
    return methodCall.getType();
  }

  public findRootOfChain(node: Node): Node {
    const expression = Node.isCallExpression(node) ? node.getExpression() : null;
    if (expression && Node.isIdentifier(expression)) return node;
    let currentNode = node;
    while (Node.isCallExpression(currentNode)) {
      const expr = currentNode.getExpression();
      if (Node.isPropertyAccessExpression(expr)) {
        const objectExpr = expr.getExpression();
        if (Node.isIdentifier(objectExpr)) return currentNode;
        if (Node.isCallExpression(objectExpr)) currentNode = objectExpr;
        else break;
      } else break;
    }
    return currentNode;
  }

  public getMethodCallsInChain(rootNode: Node): ChainElement[] {
    try {
      // Create the collector with debug enabled
      const methodChainCollector = new MethodChainCollector(true);

      // Get the source file containing this node
      const sourceFile = rootNode.getSourceFile();
      logger.debug(`Looking for method calls in file: ${sourceFile.getFilePath()}`);

      // Get all call expressions in the file that might be related to our chain
      const callExpressions = sourceFile.getDescendantsOfKind(SyntaxKind.CallExpression);

      // Find the call expression that matches our root node to determine the chain
      for (const callExpr of callExpressions) {



        if (callExpr.getPos() === rootNode.getPos()) {
          // Found the matching call expression, collect the chain
          logger.debug(`Found matching call expression at position ${callExpr.getPos()}`);

          // Select the appropriate collection method based on direction
          let chain: ChainElement[] = [];
          if (this.walkChainDownwards) {
            chain = methodChainCollector.collectChainMethodCallsDownwards(callExpr);
          } else {
            chain = methodChainCollector.collectChainMethodCallsUpwards(callExpr);
          }

          // Log results for debugging
          this.logChainElementsDebug(rootNode, chain);

          // return chain;
        }
      }

      // If we reach here, we couldn't find the matching call expression
      // Fall back to the original approach
      logger.debug(`No matching call expression found, using direct approach`);
      let chain: ChainElement[] = [];
      if (this.walkChainDownwards) {
        chain = methodChainCollector.collectChainMethodCallsDownwards(rootNode);
      } else {
        chain = methodChainCollector.collectChainMethodCallsUpwards(rootNode);
      }

      this.logChainElementsDebug(rootNode, chain);
      return chain;
    } catch (error) {
      logger.warn(`Error collecting method chain: ${error instanceof Error ? error.message : String(error)}`);
      return [];
    }
  }

  /**
   * Log debug information for chain elements (method calls and comments).
   */
  private logChainElementsDebug(rootNode: Node, chainElements: ChainElement[]): void {
    const textLimit = 20;
    const commentLimit = 50;
    const showText = false;
    logger.debug(`getMethodCallsInChain: rootNode kind=${rootNode.getKindName()}, ${showText ? `text='${rootNode.getText().substring(0, textLimit)}...'` : ''}`);
    logger.debug(`getMethodCallsInChain: Found ${chainElements.length} elements in chain:`);
    chainElements.forEach((el, idx) => {
      if (el.kind === 'comment') {
        logger.debug(`  [${idx}] COMMENT: ${el.node.text?.substring(0, commentLimit)}...`);
      } else {
        let methodName = 'root';
        if (Node.isCallExpression(el.node)) {
          const expr = el.node.getExpression();
          if (Node.isPropertyAccessExpression(expr)) {
            methodName = expr.getName();
          }
        }
        logger.debug(`  [${idx}] kind=${el.node.getKindName()}, method=${methodName}, ${showText ? `text='${el.node.getText().substring(0, textLimit)}...'` : ''}`);
      }
    });
  }
}

