      "name": "useMemo",
      "description": "Wrap expression in React useMemo",
      "body": "React.useMemo(() => {{expr}}, []);",
      "when": [ "expression" ]
    },
    {
      "name": "useCallback",
      "description": "Wrap function in React useCallback",
      "body": "React.useCallback({{expr}}, []);",
      "when": [ "function-call" ]
    },
    {
      "name": "promise",
      "description": "Create new Promise wrapper",
      "body": "new Promise((resolve, reject) => {{expr}})",
      "when": [ "expression" ]
    },
    {
      "name": "async",
      "description": "Wrap in async IIFE",
      "body": "(async () => { {{expr}} })()",
      "when": [ "expression" ]
