      "name": "Markdown Component",
      "customVariables": [ "description" ],
      "structure": [
        {
          "fileName": "<FTName>.ts",
          "template": "MarkdownComponent"
        },
        {
          "fileName": "tests/<FTName>.test.ts",
          "template": "ComponentTest"
        },
        {
          "fileName": "README.md",
          "template": "ComponentReadme"
        }
      ],
      "openFilesWhenDone": [
